mapper.select:
select
USER_ID,AREA_ID,EXAMBATCH_ID,SCH_ID,CLS_ID,ALL_TOTAL,CITY_CODE,TYPE,CLS_RANK,CITY_RANK,GRD_RANK,FULL_SCORE
,areaname as area_name,schoolname as sch_name, classname as cls_name
from RPT_EXP_STUDENT_ALLSCORE
where EXAMBATCH_ID = #{exambatchId,jdbcType=VARCHAR} and CITY_CODE = #{cityCode,jdbcType=VARCHAR}

	and student_type = #{stuType}


	and AREA_ID = #{areaId,jdbcType=VARCHAR}


	and SCH_ID = #{schoolId,jdbcType=VARCHAR}


	and CLS_ID = #{classesId,jdbcType=VARCHAR}


	and TYPE = #{flag,jdbcType=NUMERIC}

select
distinct SCH_ID,schoolname as sch_name ,area_id ,areaname as area_name
from RPT_EXP_STUDENT_ALLSCORE
where EXAMBATCH_ID = #{exambatchId,jdbcType=VARCHAR} and CITY_CODE = #{cityCode,jdbcType=VARCHAR}

	and student_type = #{stuType}


	and AREA_ID = #{areaId,jdbcType=VARCHAR}


	and SCH_ID = #{schoolId,jdbcType=VARCHAR}


	and CLS_ID = #{classesId,jdbcType=VARCHAR}


	and TYPE = #{flag,jdbcType=NUMERIC}

order by sch_id

select
distinct AREA_ID,areaname
from RPT_EXP_STUDENT_ALLSCORE
where EXAMBATCH_ID = #{exambatchId,jdbcType=VARCHAR} and CITY_CODE = #{cityCode,jdbcType=VARCHAR}

	and student_type = #{stuType}


	and AREA_ID = #{areaId,jdbcType=VARCHAR}


	and SCH_ID = #{schoolId,jdbcType=VARCHAR}


	and CLS_ID = #{classesId,jdbcType=VARCHAR}


	and TYPE = #{flag,jdbcType=NUMERIC}

order by area_id

select
AREA_ID,EXAMBATCH_ID,SCH_ID,CLS_ID,SUBJECT,SIGN_TOTAL,CITY_CODE,TYPE,SCHOOLNAME AS SCH_NAME,AREANAME
AS AREA_NAME,CLASSNAME AS CLS_NAME
from RPT_EXP_STUDENT_SUBJECT
where EXAMBATCH_ID = #{exambatchId,jdbcType=VARCHAR} and CITY_CODE = #{cityCode,jdbcType=VARCHAR}
and SUBJECT = #{subject,jdbcType=VARCHAR} and paper_id = #{paperId}

	and student_type = #{stuType}


	and AREA_ID = #{areaId,jdbcType=VARCHAR}


	and SCH_ID = #{schoolId,jdbcType=VARCHAR}


	and CLS_ID = #{classesId,jdbcType=VARCHAR}

select
distinct SCH_ID,SCHOOLNAME AS SCH_NAME,AREA_ID,AREANAME AS AREA_NAME
from RPT_EXP_STUDENT_SUBJECT
where EXAMBATCH_ID = #{exambatchId,jdbcType=VARCHAR} and CITY_CODE = #{cityCode,jdbcType=VARCHAR}
and SUBJECT = #{subject,jdbcType=VARCHAR} and paper_id = #{paperId}

	and student_type = #{stuType}


	and AREA_ID = #{areaId,jdbcType=VARCHAR}


	and SCH_ID = #{schoolId,jdbcType=VARCHAR}


	and CLS_ID = #{classesId,jdbcType=VARCHAR}

order by sch_id

select
distinct AREA_ID,areaname as area_name
from RPT_EXP_STUDENT_SUBJECT
where EXAMBATCH_ID = #{exambatchId,jdbcType=VARCHAR} and CITY_CODE = #{cityCode,jdbcType=VARCHAR}
and SUBJECT = #{subject,jdbcType=VARCHAR} and paper_id = #{paperId}

	and student_type = #{stuType}


	and AREA_ID = #{areaId,jdbcType=VARCHAR}


	and SCH_ID = #{schoolId,jdbcType=VARCHAR}


	and CLS_ID = #{classesId,jdbcType=VARCHAR}

order by area_id

select FULL_SCORE,ALL_TOTAL from RPT_EXP_STUDENT_ALLSCORE
