	and student_type = #{stuType}


	and AREA_ID = #{areaId,jdbcType=VARCHAR}


	and SCH_ID = #{schoolId,jdbcType=VARCHAR}


	and CLS_ID = #{classesId,jdbcType=VARCHAR}


